defmodule NotQuiteLisp do

    def balance_fold(c, acc) do
        case c do
            "(" -> acc + 1
            ")" -> acc - 1
        end
    end

    def balance(s) do
        s
        |> String.graphemes
        |> List.foldl(0, &balance_fold/2)
    end

    def basement_position_reduce(c, {bal, pos}) do
        { balance_fold(c, bal), pos + 1 }
    end

    def basement_position(s) do
        s
        |> String.graphemes
        |> Stream.scan(0, &balance_fold/2)
        |> Enum.find_index(&(&1 == -1))
        |> Kernel.+(1)
    end

end

test_input = [
    "(())",
    "()()",
    "(((",
    "(()(()(",
    "))(((((",
    "())",
    "))(",
    ")))",
    ")())())"
]

test_output = [
    0,
    0,
    3,
    3,
    3,
    -1,
    -1,
    -3,
    -3,
]

true = test_output == Enum.map(test_input, &NotQuiteLisp.balance/1)

