defmodule NotQuiteLisp do

    def parens_fold(c, acc) do
        case c do
            "(" -> acc + 1
            ")" -> acc - 1
        end
    end

    def balance(s) do
        s
        |> String.graphemes
        |> List.foldl(0, &parens_fold/2)
    end

end

test_input = [
    "(())",
    "()()",
    "(((",
    "(()(()(",
    "))(((((",
    "())",
    "))(",
    ")))",
    ")())())"
]

test_output = [
    0,
    0,
    3,
    3,
    3,
    -1,
    -1,
    -3,
    -3,
]

true = test_output == Enum.map(test_input, &NotQuiteLisp.balance/1)

